(()=>{"use strict";document.querySelector("#add-todo").addEventListener("click",(()=>{event.preventDefault(),function(){let e={title:document.querySelector("#form-title").value,description:document.querySelector("#form-description").value,dueDate:document.querySelector("#form-due-date").value,priority:document.querySelector("#form-priority").value};const t=document.querySelector(".todo-display"),o=document.createElement("div");o.className="todo",o.classList.add(e.title),o.innerHTML=`\n        <h3 id='todo-title'>${e.title}</h3>\n        <p id='todo-description'>${e.description}</p>\n        <p id='todo-due-date'>${e.dueDate}</p>\n        <p id='todo-priority'>${e.priority}</p>\n        <button id='delete-button'>x</button>\n    `,t.appendChild(o)}(),console.log("click")}))})();